import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import 'bootstrap/dist/css/bootstrap.min.css';

const formatHarga = (harga) => {
  return `Rp. ${Number(harga).toLocaleString('id-ID')}`;
};

const BarangList = ({ onDelete, onEdit, onAdd, onSelect }) => {
  const [barangs, setBarangs] = useState([]);

  useEffect(() => {
    // Load data from local storage when the component mounts
    const storedBarangs = JSON.parse(localStorage.getItem('barangs')) || [];
    setBarangs(storedBarangs);
  }, []);

  useEffect(() => {
    // Save data to local storage whenever barangs state changes
    localStorage.setItem('barangs', JSON.stringify(barangs));
  }, [barangs]);

  const handleDelete = (id) => {
    const updatedBarangs = barangs.filter(barang => barang.id !== id);
    setBarangs(updatedBarangs);
    onDelete(id);
  };

  const handleEdit = (barang) => {
    // Assuming onEdit modifies the barang data and returns the updated barang
    const updatedBarang = onEdit(barang);
    const updatedBarangs = barangs.map(b => b.id === updatedBarang.id ? updatedBarang : b);
    setBarangs(updatedBarangs);
  };

  const handleAdd = (barang) => {
    const updatedBarangs = [...barangs, barang];
    setBarangs(updatedBarangs);
    onAdd(barang);
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">List of Barang</h2>
      <div className="card">
        <div className="card-body">
          <div className="table-responsive">
            <table className="table table-striped">
              <thead className="thead-dark">
                <tr>
                  <th>ID</th>
                  <th>Nama Barang</th>
                  <th>Jenis Barang</th>
                  <th>Harga Barang</th>
                  <th>Stok Barang</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {barangs.map((barang) => (
                  <tr key={barang.id} onClick={() => onSelect(barang)}>
                    <td>{barang.id}</td>
                    <td>{barang.namaBarang}</td>
                    <td>{barang.jenisBarang}</td>
                    <td>{formatHarga(barang.hargaBarang)}</td>
                    <td>{barang.stokBarang}</td>
                    <td className="d-flex justify-content-center">
                      <button
                        className="btn btn-primary mr-2"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleEdit(barang);
                        }}
                      >
                        Edit
                      </button>
                      <button
                        className="btn btn-danger"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleDelete(barang.id);
                        }}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
      <button className="btn btn-success mt-3" onClick={onAdd}>
        Add New Barang
      </button>
    </div>
  );
};

BarangList.propTypes = {
  onDelete: PropTypes.func.isRequired,
  onEdit: PropTypes.func.isRequired,
  onAdd: PropTypes.func.isRequired,
  onSelect: PropTypes.func.isRequired,
};

export default BarangList;
