import React, { useState, useEffect } from 'react';
import roles from '../../data/role.json';

const AdminPage = ({ barangs}) => {
  const [newUserUsername, setNewUserUsername] = useState('');
  const [newUserPassword, setNewUserPassword] = useState('');
  const [newUserRole, setNewUserRole] = useState('user'); // Default role
  const [currentPage, setCurrentPage] = useState('gudang'); // Default page
  const [availableRoles, setAvailableRoles] = useState([]);
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    setAvailableRoles(roles.roles.map(role => role.role)); // Adjust to access the roles array inside roles JSON
    const storedUsers = localStorage.getItem('users');
    if (storedUsers) {
      setUsers(JSON.parse(storedUsers));
    }
  }, []);

  const addUser = (username, password, role) => {
    const newUser = { username, password, role };
    const updatedUsers = [...users, newUser]; // Update the users array
    setUsers(updatedUsers);
    localStorage.setItem('users', JSON.stringify(updatedUsers)); // Update localStorage
  };

  const deleteUser = (username) => {
    const updatedUsers = users.filter(user => user.username !== username);
    setUsers(updatedUsers);
    localStorage.setItem('users', JSON.stringify(updatedUsers));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newUserUsername && newUserPassword && newUserRole) {
      addUser(newUserUsername, newUserPassword, newUserRole);
      setNewUserUsername('');
      setNewUserPassword('');
      setNewUserRole('user'); // Reset role to default
      setError(null);
    } else {
      setError('Please fill in all fields');
    }
  };

  const navigateTo = (page) => {
    setCurrentPage(page);
  };

  return (
    <div className="container mt-4">
      <div className="card mt-4">
        <div className="card-header">Add New User</div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="newUserUsername">Username:</label>
              <input 
                type="text" 
                className="form-control" 
                id="newUserUsername" 
                value={newUserUsername} 
                onChange={(e) => setNewUserUsername(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="newUserPassword">Password:</label>
              <input 
                type="password" 
                className="form-control" 
                id="newUserPassword" 
                value={newUserPassword} 
                onChange={(e) => setNewUserPassword(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="newUserRole">Role:</label>
              <select 
                className="form-control" 
                id="newUserRole" 
                value={newUserRole} 
                onChange={(e) => setNewUserRole(e.target.value)}
              >
                {availableRoles.map(role => (
                  <option key={role} value={role}>{role}</option>
                ))}
              </select>
            </div>
            {error && <div className="alert alert-danger">{error}</div>}
            <button type="submit" className="">Add User</button>
          </form>
        </div>
      </div>
      <div className="card mt-4">
        <div className="card-header">Stock</div>
        <div className="card-body">
        <table className="table">
        <thead>
          <tr>
            <th>Nama Barang</th>
            <th>Stok</th>
          </tr>
        </thead>
        <tbody>
          {barangs.map(barang => (
            <tr key={barang.id}>
              <td>{barang.namaBarang}</td>
              <td>{barang.stokBarang}</td>
            </tr>
          ))}
        </tbody>
      </table>
        </div>
      </div>
      <div className="card mt-4">
        <div className="card-header">Users</div>
        <div className="card-body">
          <ul>
            {users.map(user => (
              <li key={user.username}>
                {user.username} ({user.role}) 
                <span className="ml-6"> </span>
                <button onClick={() => deleteUser(user.username)} className="">Delete</button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default AdminPage;
