import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import users from '../../data/users.json';

const AdminPage = () => {
  const [usersData, setUsersData] = useState(users);

  return (
    <div className="container mt-4">
      <div className="card mt-4">
        <div className="card-header">
          <h5 className="mb-0">User Management</h5>
        </div>
        <div className="card-body">
          <div className="d-flex justify-content-between align-items-center mb-3">
            <h5 className="mb-0">Users</h5>
            <Link to="/admin/add-user" className="btn btn-primary">Add User</Link>
          </div>

          <table className="table table-striped">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Avatar</th>
                <th scope="col">Username</th>
                <th scope="col">Email</th>
                <th scope="col">Nama</th>
                <th scope="col">Action</th>
              </tr>
            </thead>
            <tbody>
              {usersData.map((user, index) => (
                <tr key={user.id}>
                  <th scope="row">{index + 1}</th>
                  <td>
                    <img src={user.avatar} alt={user.name} className="rounded-circle" style={{ width: '40px', height: '40px' }} />
                  </td>
                  <td>{user.username}</td>
                  <td>{user.email}</td>
                  <td>{user.name}</td>
                  <td>
                    <Link to={`/admin/edit-user/${user.id}`} className="btn btn-sm btn-warning">Edit</Link>
                    <button className="btn btn-sm btn-danger" onClick={() => handleDelete(user.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  const handleDelete = (userId) => {
    const updatedUsers = usersData.filter((user) => user.id !== userId);
    setUsersData(updatedUsers);
  };
};

export default AdminPage;
