import React, { useState } from 'react';
import './SupplierPage.css';

const formatHarga = (harga) => `Rp. ${Number(harga).toLocaleString('id-ID')}`;

const SupplierPage = ({ barangs, onUpdateStock }) => {
  const [selectedBarangId, setSelectedBarangId] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [receipt, setReceipt] = useState(null);

  const handlePurchase = () => {
    const barang = barangs.find(b => b.id === selectedBarangId);
    if (barang && quantity > 0) {
      const total = barang.hargaBarang * quantity;
      const newReceipt = {
        barang: barang.namaBarang,
        harga: formatHarga(barang.hargaBarang),
        quantity,
        total: formatHarga(total),
      };
      setReceipt(newReceipt);
      setSelectedBarangId(null);
      setQuantity(1);
    } else {
      alert('Please select a barang and enter a valid quantity.');
    }
  };

  return (
    <div className="supplier-container">
      <h2 className="title">Purchase Barang</h2>
      <div className="form-group">
        <label htmlFor="barangSelect">Select Barang:</label>
        <select
          id="barangSelect"
          className="form-control"
          value={selectedBarangId || ''}
          onChange={(e) => setSelectedBarangId(parseInt(e.target.value))}
        >
          <option value="" disabled>Select Barang</option>
          {barangs.map(barang => (
            <option key={barang.id} value={barang.id}>
              {barang.namaBarang} - {formatHarga(barang.hargaBarang)}
            </option>
          ))}
        </select>
      </div>
      <div className="form-group">
        <label htmlFor="quantity">Quantity:</label>
        <input
          type="number"
          id="quantity"
          className="form-control"
          value={quantity}
          onChange={(e) => setQuantity(parseInt(e.target.value))}
          min="1"
        />
      </div>
      <button className="btn btn-primary" onClick={handlePurchase}>
        Purchase
      </button>

      {receipt && (
        <div className="receipt-container">
          <h3>Receipt</h3>
          <p>Barang: {receipt.barang}</p>
          <p>Harga: {receipt.harga}</p>
          <p>Quantity: {receipt.quantity}</p>
          <p>Total: {receipt.total}</p>
        </div>
      )}
    </div>
  );
};

export default SupplierPage;
